var onLoginRequest = function(context) {
	executeStep(1, {
		onFail: function (context) {

			var authenticatorStatus = context.request.params.scenario; 

			// If it is a passkey progressive enrollment request trigger the following flow.
			if (authenticatorStatus != null && authenticatorStatus[0] == 'INIT_FIDO_ENROLL' ) { 

				var filteredAuthenticationOptions = filterAuthenticators(context.steps[1].options);
				executeStep(1, {
					stepOptions: {
						markAsSubjectIdentifierStep: 'true',
						markAsSubjectAttributeStep: 'true'
					},
					authenticationOptions: filteredAuthenticationOptions
					},{
					onSuccess: function (context) {
						// If user got successfully authenticated 
						executeStep(1, { 
							stepOptions: { 
								forceAuth: 'true'
							},
							authenticationOptions: [{authenticator:'FIDOAuthenticator'}]
						}, {});
					},
				});
			}
		}
	});
};

var filterAuthenticators = function(stepOptionsString) {

	stepOptionsString = stepOptionsString.toString();

	var excludeAuthenticator = 'FIDOAuthenticator';

	var idpMatches = stepOptionsString.match(/idp=[^,]+/g) || [];
	var idpValues = idpMatches.map(function(item) {
		return item.split('=')[1];
	});

	var authenticatorMatches = stepOptionsString.match(/authenticator=[^,}]+/g) || [];
	var authenticatorValues = authenticatorMatches.map(function(item) {
		return item.split('=')[1];
	});

	var result = [];

	for (var i = 0; i < idpValues.length; i++) {
		var idp = idpValues[i];
		var authenticator = authenticatorValues[i];

		if (idp === 'LOCAL' && authenticator !== excludeAuthenticator) {
			result.push({'authenticator': authenticator});
		} else if (idp !== 'LOCAL') {
			result.push({'idp': idp});
		}
	}

	return result;
};
